import React from "react";
import {
  BarChart as RechartsBarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
} from "recharts";

interface DataPoint {
  name: string;
  value: number;
  previous?: number;
}

interface BarChartProps {
  data: DataPoint[];
  title?: string;
  color?: string;
  showComparison?: boolean;
}

export function BarChart({ data, title, color = "#6366f1", showComparison = false }: BarChartProps) {
  return (
    <div className="chart-container">
      {title && <h3 className="chart-title">{title}</h3>}
      <ResponsiveContainer width="100%" height={300}>
        <RechartsBarChart data={data} margin={{ top: 5, right: 30, left: 20, bottom: 5 }}>
          <CartesianGrid strokeDasharray="3 3" stroke="var(--border-color)" />
          <XAxis dataKey="name" stroke="var(--text-secondary)" />
          <YAxis stroke="var(--text-secondary)" />
          <Tooltip
            contentStyle={{ background: "var(--bg-primary)", border: "1px solid var(--border-color)" }}
          />
          <Legend />
          <Bar dataKey="value" fill={color} radius={[4, 4, 0, 0]} name="Current" />
          {showComparison && (
            <Bar dataKey="previous" fill="var(--text-muted)" radius={[4, 4, 0, 0]} name="Previous" />
          )}
        </RechartsBarChart>
      </ResponsiveContainer>
    </div>
  );
}

.chart-container {
  background: var(--bg-secondary);
  border: 1px solid var(--border-color);
  border-radius: 8px;
  padding: 16px;
  margin: 16px 0;
}

.chart-title {
  margin: 0 0 12px;
  font-size: 14px;
  font-weight: 600;
  color: var(--text-primary);
}
